sys_script_email.script:
// Construct a GlideRecord query to find all NeedIt Task records with Due date
// field values in the past for the same Assigned to.  The records cannot be in the
// Closed state.
var rightNow = new GlideDateTime();

var niTaskCount = new GlideAggregate('x_58872_needit_needit_task');
niTaskCount.addAggregate('COUNT');
niTaskCount.addQuery('due_date','<=',rightNow);
niTaskCount.addQuery('state','<',3);
niTaskCount.addQuery('assigned_to',current.assigned_to);
niTaskCount.query();

var count = 0;
if (niTaskCount.next()){
	count = niTaskCount.getAggregate('COUNT');
}

if (count > 1){
	// Print to the Message field on a notification
	template.print("In addition to this overdue NeedIt Task, you have " + (count - 1) + " more overdue NeedIt Tasks.");
}
